					.setDescription(atkListed.join("\n"));
                embedMessage.edit(TempAttackembed);
			}

			var timeoutID = setInterval(battle, 1500);

            async function battle() {

                if (playerOneHealth > 0 && playerTwoHealth > 0) {

                    const tempAttack = possibleActions[Math.floor(Math.random() * possibleActions.length)];
                    const attackName = Object.getOwnPropertyNames(tempAttack)[0];
                    const attackDmg = tempAttack[attackName].damage;
                    var attackFormat = tempAttack[attackName].format;

                    if (currentPlayerGoing == 1) {
                        playerTwoHealth = playerTwoHealth - attackDmg;
                        if (attacksListed.length < 3) {
                            attackFormat = await attackFormat.replace(/{player1}/g,"__" + playerOne.username + "__");
                            attackFormat = await attackFormat.replace(/{player2}/g,"__" + playerTwo.username + "__");
