                            currentPlayerGoing = 2;
                            embedColor = 0x00ff00;
                            updateEmbed(attacksListed);
                        } else {
                            attackFormat = await attackFormat.replace(/{player1}/g,"__" + playerOne.username + "__");
                            attackFormat = await attackFormat.replace(/{player2}/g,"__" + playerTwo.username + "__");
                            attacksListed.splice(0, 1);
                            attacksListed.push("🔹 " + attackFormat + " (" + attackDmg + " DMG)");
                            currentPlayerGoing = 2;
                            embedColor = 0x00ff00;
                            updateEmbed(attacksListed);
                        }
                    } else {
                        playerOneHealth = playerOneHealth - attackDmg;
                        if (attacksListed.length < 3) {
                            attackFormat = await attackFormat.replace(/{player1}/g,"__" + playerTwo.username + "__");
                            attackFormat = await attackFormat.replace(/{player2}/g,"__" + playerOne.username);
                            attacksListed.push("🔸" + attackFormat + " (" + attackDmg + " DMG)");
                            currentPlayerGoing = 1;
                            embedColor = 0xff0000;
                            updateEmbed(attacksListed);
                        } else {
                            attackFormat = await attackFormat.replace(/{player1}/g, "__" + playerTwo.username + "__");
                            attackFormat = await attackFormat.replace(/{player2}/g,"__" + playerOne.username + "__");
                            attacksListed.splice(0, 1);
                            attacksListed.push("🔸" + attackFormat + " (" + attackDmg + " DMG)");
                            currentPlayerGoing = 1;
                            embedColor = 0xff0000;
                            updateEmbed(attacksListed);
                        }
                    }

                } else {
                    if (playerTwoHealth <= 0) {
                        if (attacksListed.length < 3) {
                            attacksListed.splice(0, 1);
                            attacksListed.push("🏆 __" + playerOne.username + "__ has won!");
							playerTwoHealth = 0;
							embedColor = 0xffff00;
                            endEmbed(attacksListed);
							clearInterval(timeoutID);
                        } else {
                            attacksListed.push("🏆 __" + playerOne.username + "__ has won!");
							playerTwoHealth = 0;
							embedColor = 0xffff00;
                            updateEmbed(attacksListed);
                            endEmbed(attacksListed);
							clearInterval(timeoutID);
                        }
                    } else if (playerOneHealth <= 0) {
                        if (attacksListed.length < 3) {
                            attacksListed.splice(0, 1);
                            attacksListed.push("🏆 __" + playerTwo.username + "__ has won!");
							playerOneHealth = 0;
							embedColor = 0xffff00;
							endEmbed(attacksListed);
							clearInterval(timeoutID);
                        } else {
                            attacksListed.push("🏆 __" + playerTwo.username + "__ has won!");
							playerOneHealth = 0;
							embedColor = 0xffff00;
                            endEmbed(attacksListed);
							clearInterval(timeoutID);
                        }
                    }
                }
            }


        });

}

